<template> <div> <h2>Options Contracts Input</h2> <div v-for="(option, index) in options" :key="index" class="option-input"> <label>Contract {{ index + 1 }}</label> <input v-model.number="option.strike" placeholder="Strike Price" type="number" /> <input v-model.number="option.premium" placeholder="Premium" type="number" /> <select v-model="option.type"> <option value="call">Call</option> <option value="put">Put</option> </select> </div> <button @click="generateGraph">Generate Graph</button> </div> </template> <script> export default { name: 'OptionInputForm', data() { return { options: [ { strike: '', premium: '', type: 'call' }, { strike: '', premium: '', type: 'call' }, { strike: '', premium: '', type: 'call' }, { strike: '', premium: '', type: 'call' }, ], }; }, methods: { generateGraph() { this.$emit('generate-graph', this.options); }, }, }; </script> <style scoped> .option-input { margin-bottom: 10px; } </style>